 * This notice shall be included in all copies or substantial portions of the Software.
 *
 */

// event
// import Events from '../event/Events';

// tick
import Polling from './Polling';

// tick/events
import FpsEvents from './events/FpsEvents';

// /**
//  * private property key, fps を保存するための Symbol
//  * @type {Symbol}
//  * @private
//  */
// const fpsSymbol = Symbol('Singleton Fps Symbol');

/**
 * フレームレート毎に `UPDATE` イベントを発生させます
 *
 * @example
 * // 2sec. interval
 * const fps = new Fps(0.5);
 * const update = () => {
 *  // code here, something do
 * };
 * fps.on(Fps.UPDATE, update);
 * fps.start();
 * */
export default class Fps extends Polling {
  // ----------------------------------------
  // EVENT
  // ----------------------------------------
  /**
   * フレームレート毎に発生するイベント - fpsUpdate
   * @event UPDATE
   * @type {string}
   */
  static UPDATE = 'fpsUpdate';
  // /**
  //  * フレームレート毎に発生するイベントを取得します
  //  * @event UPDATE
  //  * @returns {string} event, fpsUpdate を返します
  //  * @default fpsUpdate
  //  */
  // static get UPDATE() {
  //   return 'fpsUpdate';
  // }
  // ----------------------------------------
  // CONSTRUCTOR
  // ----------------------------------------
  /**
   * 引数の frame rate に合わせ UPDATE イベントを発生させます
   * @param {number} [fps=30] frame rate, default: 30
   */
  constructor(fps = 30) {
    super(1000 / fps);
    // @type {Events} - Events
    const events = new FpsEvents(Fps.UPDATE, this, this);
    events.fps = fps;
    /**
     * Fps.UPDATE Events instance
     * @type {Events}
     */
    this.events = events;
    /**
     * frame rate
     * @type {number}
     */
    this.fps = fps;
  }
  // ----------------------------------------
  // METHOD
  // ----------------------------------------
  /**
   * fps を変更します
   * 1. プロパティ polling 変更
   * 1. 継承 method update 実行
   * @param {number} interval fps
   * @returns {boolean} 継承 method `update` をコールし UPDATE event が発生すると true を返します
   */
  change(interval) {
    /**
     * polling 間隔
     * @type {number}
     */
    this.interval = 1000 / interval;
    this.fps = interval;
    this.events.fps = interval;
    // return this.update();
    return this.start();
  }
}

