// --------------------------------
// constant for calculate
/**
 * 計算用定数 - 1 minute
 * ```
 * 1000 * 60
 * ```
 * @type {number}
 * @private
 * @static
 */
const oneMinute = 1000 * 60;
/**
 * 計算用定数 - 1 hour
 * ```
 * 1000 * 60 * 60
 * ```
 * @type {number}
 * @private
 * @static
 */
const oneHour = oneMinute * 60;
/**
 * 計算用定数 - 1 day
 * ```
 * 1000 * 60 * 60 * 24
 * ```
 * @type {number}
 * @private
 * @static
 */
const oneDay = oneHour * 24;
/**
 * 計算用定数 - 1 week
 * ```
 * 1000 * 60 * 60 * 24 * 7
 * ```
 * @type {number}
 * @private
 * @static
 */
const oneWeek = oneDay * 7;
/**
 * 計算用定数 - 1 month
 * ```
 * 1000 * 60 * 60 * 24 * 30
 * ```
 * @type {number}
 * @private
 * @static
 */
const oneMonth = oneDay * 30;

/**
 * 指定時間を計算し Date instance を作成します
 */
export default class Times {
  // /**
  //  * 1970-1-1 00:00
  //  * @returns {Date} 1970-1-1 00:00
  //  */
  // static standard() {
  //   return new Date();
  // }
  /**
   * 現在時間 Date instance
   * @returns {Date} 現在時間 Date instance
   */
  static present() {
    return new Date();
  }
  /**
   * 指定分後の Date instance
   * @param {number} n 指定分
   * @returns {Date} 指定分後の Date instance
   */
  static minute(n) {
    return new Date(Date.now() + (n * oneMinute));
  }
  /**
   * 指定時間後の Date instance
   * @param {number} n 指定時間
   * @returns {Date} 指定時間後の Date instance
   */
  static hour(n) {
    return new Date(Date.now() + (n * oneHour));
  }
  /**
   * 指定日後の Date instance
   * @param {number} n 指定日
   * @returns {Date} 指定日後の Date instance
   */
  static day(n) {
    return new Date(Date.now() + (n * oneDay));
  }
  /**
   * 指定週後の Date instance
   * @param {number} n 指定週
   * @returns {Date} 指定週後の Date instance
   */
  static week(n) {
    return new Date(Date.now() + (n * oneWeek));
  }
  /**
   * 指定月後の Date instance
   * @param {number} n 指定月
   * @returns {Date} 指定月後の Date instance
   */
  static month(n) {
    return new Date(Date.now() + (n * oneMonth));
  }
}

