/**
 * 固定値を使用し fps を決定します
 *
 * 以下のフレームレートが設定可能です
 *
 * - 60: Rate.RATE_60
 * - 30: RATE_30
 * - 20: RATE_20
 * - 15: RATE_15
 * - 12: RATE_12
 * - 10: RATE_10
 * - 6: RATE_6
 * - 5: RATE_5
 *
 */
export default class Rate extends Polling {
  /**
   * 固定値フレームレート毎に UPDATE イベントを発生させます
   * @param {number} rateValue fps, 固定値以外設定できません
   */
  constructor(rateValue) {
    super(1000 / 60);
    // @type {Events - Events
    const events = new Events(Rate.UPDATE, this, this);
    // events.rate = rate;
    // /**
    //  * Rate.UPDATE Events instance
    //  * @type {Events}
    //  */
    // this.events = events;

    // @type {number} - count, rate に達したかを計測するための counter 変数
    // this[countSymbol] = 0;

    // correct rate list
    // サポートするレートリスト
    // this[ratesSymbol] = [
    //   Rate.RATE_60,
    //   Rate.RATE_30,
    //   Rate.RATE_20,
    //   Rate.RATE_15,
    //   Rate.RATE_12,
    //   Rate.RATE_10,
    //   Rate.RATE_6,
    //   Rate.RATE_5,
    // ];
    //
    // // frame rate
    // this[rateSymbol] = 0;
    const rates = [
      Rate.RATE_60,
      Rate.RATE_30,
      Rate.RATE_20,
      Rate.RATE_15,
      Rate.RATE_12,
      Rate.RATE_10,
      Rate.RATE_6,
      Rate.RATE_5,
    ];
    /**
     * @returns {Events} Events instance
     */
    this.events = () => events;
    /**
     * 許容可能な rate
     * @return {Array<number>} 許容可能な rate
     */
    this.rates = () => rates;
    /**
     * rate count, update 毎にカウントアップします
     * @type {number}
     */
    this.count = 0;
    let rate = null;
    /**
     * rate 値
     * @returns {?number} rate 値
     */
    this.rate = () => rate;
    /**
     * rate 値を設定します
     * @param {number} value rate 値
     * @returns {boolean} rate 設定に成功すると true を返します
     */
    this.setRate = (value) => {
      if (this.validate(rateValue)) {
        rate = value;
        return true;
      }
      return false;
    };
    // init
    this.setRate(rate);
  }
  // ----------------------------------------
  // CONST
  // ----------------------------------------
  /**
   * fps 60 基準値を取得します
   * @const RATE_60
   * @returns {number} fps 60 基準値を返します
   * @default 1
   */
  static get RATE_60() {
    return 1;
  }  /**
   * fps 30 基準値を取得します
   * @const RATE_30
   * @returns {number} fps 30 基準値を返します
   * @default 2
   */
  static get RATE_30() {
    return 2;
  }
  /**
   * fps 20 基準値を取得します
   * @const RATE_20
   * @returns {number} fps 20 基準値を返します
   * @default 3
   */
  static get RATE_20() {
    return 3;
  }
  /**
   * fps 15 基準値を取得します
   * @const RATE_15
   * @returns {number} fps 15 基準値を返します
   * @default 4
   */
  static get RATE_15() {
    return 4;
  }
  /**
   * fps 12 基準値を取得します
   * @const RATE_12
   * @returns {number} fps 12 基準値を返します
   * @default 5
   */
  static get RATE_12() {
    return 5;
  }
  /**
   * fps 10 基準値を取得します
   * @const RATE_10
   * @returns {number} fps 10 基準値を返します
   * @default 6
   */
  static get RATE_10() {
    return 6;
  }
  /**
   * fps 6 基準値を取得します
   * @const RATE_6
   * @returns {number} fps 6 基準値を返します
   * @default 10
   */
  static get RATE_6() {
    return 10;
  }
  /**
   * fps 5 基準値を取得します
   * @const RATE_5
   * @returns {number} fps 6 基準値を返します
   * @default 12
   */
  static get RATE_5() {
    return 12;
  }
  // ----------------------------------------
  // EVENT
  // ----------------------------------------
  /**
   * フレームレート毎に発生するイベント type を取得します
   * @event UPDATE
   * @returns {string} event, rateUpdate を返します
   * @default rateUpdate
   */
  static get UPDATE() {
    return 'rateUpdate';
  }
  // // ----------------------------------------
  // // GETTER / SETTER
  // // ----------------------------------------
  // /**
  //  * fps 基準値リストを取得します
  //  * @returns {number} fps 基準値リストを返します
  //  */
  // get rates() {
  //   return this[ratesSymbol];
  // }
  // /**
  //  * fps 基準値を取得します
  //  * @returns {number} fps 基準値を返します
  //  */
  // get rate() {
  //   return this[rateSymbol];
  // }
  //
  // /**
  //  * rate に達したかを計測するための counter 変数を取得します
  //  * @returns {number} rate に達したかを計測するための counter 変数を返します
  //  */
  // get count() {
  //   return this[countSymbol];
  // }
  // /**
  //  * rate に達したかを計測するための counter 変数を設定します
  //  * @param {number} step rate に達したかを計測するための counter 数
  //  */
  // set count(step) {
  //   this[countSymbol] = step;
  // }
  // ----------------------------------------
  // METHOD
  // ----------------------------------------
  /**
   * 正規な rate 値かをチェックします
   * @param {number} rate 対象 rate
   * @returns {boolean} 正しいと true を返します
   */
  validate(rate) {
    return this.rates().indexOf(rate) !== -1;
  }
  /**
   * fps 基準値を設定します
   * @throws {Error} 引数 rate が設定可能値以外の時に例外エラーが発生します
   * @param {number} rate fps 基準値, <br>
   * this.RATE_30, this.RATE_20, this.RATE_15, this.RATE_12, this.RATE_10, <br>
   * this.RATE_6, this.RATE_5 の何れかが必須です
   * @returns {boolean} rate 設定に成功すると true を返します
   */
  change(rate) {
    // if (this.validate(rate)) {
    //   this.rate = rate;
    //   return true;
    // }
    // // else {
    // //   throw new Error(`illegal rate: ${rate}. use const RATE_NN`);
    // // }
    // return false;
    const result = this.setRate(rate);
    this.update();
    return result;
  }
  /**
   * loop(requestAnimationFrame) を開始します
   * @returns {boolean} start に成功すると true を返します
   */
  start() {
    const rate = this.rate();
    if (rate === null) {
      throw new Error(`rate are not correct. ${rate}`);
    }
    if (this.started) {
      // already start
      return false;
    }
    // flag -> true
    this.turnOver();
    // cycle
    this.initCycle();
    // 強制的に1回目を実行
    this.fire(this.updateEvents(0, 0));

    return true;
  }
  /**
   * loop(requestAnimationFrame) します
   * @returns {boolean} Rate.UPDATE event が発生すると true を返します
   */
  update() {
    // 余りが 0 の時にイベントを発火します
    this.count += 1;
    const reminder = this.count % this.rate();
    if (reminder === 0) {
      this.count = 0;
      this.fire(this.updateEvents(0, 0));

      return true;
    }

    return false;
  }
}

